import * as actionTypes from '../constants/cartConstants';
const state={
    cartItems:[],
    
};

export const cartReducer=( state , action=>{
    switch(action.type){
        case actionTypes.ADD_TO_CART:
            const item=action.payload;

            const existItem = state.cardItems.find((x)=> x.product === item.product)

             
             if(existItem)
             {
                 return {
                     ...state,
                     cardItems:state.cartItems.map((x)=> x.product === item.product ? item : x )
                 }
             }
             else{
                 return{
                    ...state,
                    cardItems:[...state.cardItems,item]
                 };
             }
        case actionTypes.REMOVE_FROM_CART:
            return{
                ...state,
                cardItems:state.cardItems.filter((x)=> x.product !==action.payload),
            };



        default:
            return state;
    }
})
